"use strict";
const chalk = require("chalk");
const Message = {
    /**
     *
     * @param message
     */
    error(error, reason, solution) {
        console.log(chalk.red("Error: ") + error);
        console.log(chalk.cyan("Reason: ") + reason);
        console.log(chalk.green("Solution: ") + solution);
        console.log();
    }
};
module.exports = Message;
