import util = require("util");

import IQuery = require("./interfaces/IQuery");
import IRow = require("./interfaces/IRow");
import ISheet = require("./interfaces/ISheet");

import Database = require("./Database");
import Document = require("./Document");
import Message = require("./Message");

/** Collection class */
class Collection {
  /** Database > Collection */
  public database: Database;

  /** Collection = Sheet*/
  public sheet: ISheet;

  /**
   * Create Collection object
   * @param {Database} database Database of this Collection
   * @param {ISheet} sheet Sheet
   */
  constructor(database: Database, sheet?: ISheet) {
    /** Database of this Collection */
    this.database = database;

    if (sheet) {
      /** Sheet */
      this.sheet = sheet;
    }
  }

  /**
   * Select Documents
   * @param {IQuery} query
   * @returns Document objects array
   */
  public async select(query: IQuery): Promise<Document[]> {
    const documents = new Array<Document>(new Document());
    documents.pop();
    if (this.sheet) {
      const rows: IRow[] = await util.promisify(this.sheet.getRows)(query);
      rows.forEach(row => documents.push(new Document(this, row)));
    } else {
      Message.error("Access collection failed.", "Select Documents failed.", "Access to collection first.");
    }
    return documents;
  }

  /**
   * Insert Document
   * @param {Object} document Inserted Document
   * @returns ID of inserted Document
   */
  public async insert(document: Object): Promise<string> {
    if (this.sheet) {
      const row: IRow = await util.promisify(this.sheet.addRow)(document);
      return row.id;
    } else {
      Message.error("Access collection failed.", "Insert Document failed.", "Access to collection first.");
    }
  }

  /**
   * Update Documents
   * @param {IQuery} query Select query
   * @param {Object} document Updated Document
   * @returns Number of updated Documents
   */
  public async update(query: IQuery, document: Object): Promise<number> {
    if (this.sheet) {
      let updated = 0;
      const rows: IRow[] = await util.promisify(this.sheet.getRows)(query);
      rows.forEach(row => {
        Object.assign(row, document);
        row.save();
        updated++;
      });
      await util.promisify(this.database.spreadsheet.getInfo)();
      return updated;
    } else {
      Message.error("Access collection failed.", "Update Documents failed.", "Access to collection first.");
    }
  }

  /**
   * Delete Documents
   * @param {IQuery} query Select query
   * @returns Number of deleted Documents
   */
  public async delete(query: IQuery): Promise<number> {
    if (this.sheet) {
      let deleted = 0;
      const rows: IRow[] = await util.promisify(this.sheet.getRows)(query);
      rows.forEach(row => {
        row.del();
        deleted++;
      });
      await util.promisify(this.database.spreadsheet.getInfo)();
      return deleted;
    } else {
      Message.error("Access collection failed.", "Delete Documents failed.", "Access to collection first.");
    }
  }
}

export = Collection;
