import chalk = require("chalk");

const Message = {
  /**
   *
   * @param message
   */
  error(error: string, reason: string, solution: string) {
    console.log(chalk.red("Error: ") + error);
    console.log(chalk.cyan("Reason: ") + reason);
    console.log(chalk.green("Solution: ") + solution);
    console.log();
  }
};

export = Message;
