import ICreds = require("./ICreds");
import ISheet = require("./ISheet");

/** Spreadsheet datatype interface */
interface ISpreadsheet {
  addRow: (worksheet_id: any, data: any, cb: any) => void;
  addWorksheet: (opts: any, cb: any) => any;
  getCells: (worksheet_id: any, opts: any, cb: any) => void;
  getInfo: (cb: any) => void;
  getRows: (worksheet_id: any, opts: any, cb: any) => void;
  isAuthActive: () => boolean;
  makeFeedRequest: (url_params: any, method: any, query_or_data: any, cb: any) => void;
  removeWorksheet: (sheet_id: any, cb: any) => any;
  setAuth: (username: any, password: any, cb: any) => any;
  setAuthToken: (auth_id: any) => void;
  useServiceAccountAuth: (creds: ICreds, cb: any) => any;
  info: {
    id: string;
    title: string;
    updated: string;
    author: { name: string; email: string };
    worksheets: ISheet[];
  };
  worksheets: ISheet[];
}

export = ISpreadsheet;
