SDSKit = require("../dist");

(async () => {
  const id = "106vOe8ddO5j1of7TmvQTYeZQ_lj_kp8SyQzF7NeCwTY";
  const creds = require("./creds.json");
  const print = document => {
    console.log(document.get("key"));
    console.log(document.get("name"));
    console.log(document.get("gender"));
    console.log(document.get("birthday"));
    console.log(document.get("address"));
    console.log(document.get("telephone"));
    console.log(document.get("mobile"));
    console.log();
  };

  const cluster = new SDSKit();
  const database = await cluster.access(id, creds);
  const collection = database.collection("employee");
  const documents = await collection.select({ offset: 1, orderby: "key" });
  documents.forEach(document => print(document));
})();
